import React from 'react'



const Header = ({ text }) => {
  return (
    <h3>{text}</h3>
  )
}



const Parts = ({ parts }) => {

  const returnParts = (part) => {
    console.log('Part:', part)
    return <p key={part.id}>{part.name} {part.exercises}</p>
  }

  return (
    <>
      {parts.map(returnParts)}
    </>
  )
}



const Total = ({ parts }) => {
  const calculateTotal = () => {
    console.log('Calculating...')

    const calculateSum = (initialValue, part, index) => {
      console.log('Part:', part)
      console.log('Initial Value:', initialValue)

      const sum = part.exercises
      if (index == 0) {
        const total = initialValue.exercises
      } 


      const total = initialValue


      console.log('Sum:', sum)
      console.log('Total:', total)

      return total + sum
    }


    const total = parts.reduce(calculateSum)

    console.log('Total:', total)
    return total
  }

  const total = calculateTotal()

  return (
    <strong>Total of {total} exercises</strong>
  )
}



const Content = ({ course }) => {  
  return (
    <>
      <Parts parts={course.parts} key={course.id} />
      <Total parts={course.parts} course={course} />
    </>
  )
}

const Course = ({ course }) => {
  console.log('Course:', course)
  return (
    <>
      <Header text={course.name}/>
      <Content course={course} />
    </>
  )
}



export default Course
