const Countries = ({ countries, setNewFilter }) => {

  console.log('setNewFilter', setNewFilter)

  const Country = ({ country, singleCountry, setNewFilter }) => {

    if (singleCountry) {
      return (
        <div>
          <h1>{country.name}</h1>

          <p>
            Capital: {country.capital}
            <br></br>
            Population: {country.population}
          </p>

          <h2>Languages</h2>
          <ul>
            {country.languages.map(language => 
              <li key={language.name}>{language.name}</li>)}
          </ul>
          <img 
          src={country.flag} 
          alt="Country's flag"
          width='170'
          ></img>
        </div>
      )
    } else {
      return (
    <div>
      {country.name}

      <input
      type='button'
      name={country.name}
      value='Show'
      onClick={() => console.log('Button pressed.', country.name)}/>
    </div>
      )
    }

  }


  if (countries.length > 10) {
    return (
      <div>
        Too many matches, specify another filter.
      </div>
    )
  } else if (countries.length === 1) {
    return (
      <>
        {countries.map(country => 
          <Country 
          country={country} 
          key={country.name} 
          singleCountry={true}
          />)
        }
      </>
    )
  } else {
    return (
      <div>
        {countries.map(country => 
          <Country 
          country={country} 
          key={country.name}
          singeCountry={false} />)
        }
      </div>
    )
  }
} 

export default Countries
