import React, { useState, useEffect} from 'react'
import axios from 'axios'

const api_key = process.env.REACT_APP_API_KEY


const Countries = ({ countries, setNewFilter }) => {

  const Country = ({ country, singleCountry, setNewFilter }) => {

    const [ weather, setWeather ] = useState([])

    useEffect(() => {
      console.log('Weather pending...')

      axios
        .get(`http://api.weatherapi.com/v1/forecast.json?key=${api_key}&q=${country.name}&days=1&aqi=no&alerts=no`)
        .then(response => {
          console.log(response.data)
          setWeather(response.data)
        })
      }, [])

      console.log('Rendering', weather.length, 'weather')
      console.log('Weather:', weather)

    if (singleCountry) {
      return (
        <div>
          <h1>{country.name}</h1>

          <p>
            Capital: {country.capital}
            <br></br>
            Population: {country.population}
          </p>

          <h2>Languages</h2>
          <ul>
            {country.languages.map(language => 
              <li key={language.name}>{language.name}</li>)}
          </ul>
          <img 
          src={country.flag} 
          alt="Country's flag"
          width='170'
          ></img>
        </div>
      )
    } else {
      return (
    <div>
      {country.name}

      <input
      type='button'
      name={country.name}
      value='Show'
      onClick={() => 
        console.log('Button pressed.', country.name) 
        || setNewFilter(country.name)}/>
    </div>
      )
    }

  }


  if (countries.length > 10) {
    return (
      <div>
        Too many matches, specify another filter.
      </div>
    )
  } else if (countries.length === 1) {
    return (
      <>
        {countries.map(country => 
          <Country 
          country={country} 
          key={country.name} 
          singleCountry={true}
          setNewFilter={setNewFilter}
          />)
        }
      </>
    )
  } else {
    return (
      <div>
        {countries.map(country => 
          <Country 
          country={country} 
          key={country.name}
          singeCountry={false} 
          setNewFilter={setNewFilter}/>)
        }
      </div>
    )
  }
} 





const App = () => {


  const [ countries, setCountries ] = useState([])

  const [ newFilter, setNewFilter ] = useState('')
  const [ showAll, setShowAll ] = useState(true)

  useEffect(() => {
    console.log('Countries pending...')

    axios
      .get('https://restcountries.eu/rest/v2/all')
      .then(response => {
        setCountries(response.data)
        console.log('Promise fulfilled!')
      })
  }, [])

  console.log('Rendering', countries.length, 'countries.')

  const handleFilter = (event) => {
    console.log('Event:', event.target.value)
    setNewFilter(event.target.value)

    if (newFilter !== '') {
      setShowAll(false)
    }


  }

  const filteredCountries = showAll 
    ? countries 
    : countries.filter(country => country.name.toLowerCase().includes(newFilter.toLowerCase()))


  return (
    <div>
      Find countries: 
      <input 
      value={newFilter}
      onChange={handleFilter}
      />

      <Countries countries={filteredCountries} setNewFilter={setNewFilter}/>

    </div>
  )
}

export default App
