import React, { useState, useEffect} from 'react'
import axios from 'axios'



const Countries = ({ countries }) => {

  const Country = ({ country }) => {
    return (
      <div>
        {country.name}
      </div>
    )
  }


  return (
    <>
      {countries.map(country => <Country country={country} key={country.name}/>)}
    </>
  )
} 


const App = () => {

  const [ countries, setCountries ] = useState([])
  const [ newFilter, setNewFilter ] = useState('')
  const [ shownCountries, setShownCountries] = ([])

  useEffect(() => {
    console.log('Pending...')

    axios
      .get('https://restcountries.eu/rest/v2/all')
      .then(response => {
        setCountries(response.data)
        console.log('Promise fulfilled!')
      })
  }, [])

  const handleFilter = (event) => {
    console.log(event.target.value)
    setNewFilter(event.target.value)
  }

  const filteredCountries = (countries.filter(country => country.name.includes(newFilter)))
  console.log('Filtered Countries:', shownCountries)
  setShownCountries(filteredCountries)

  return (
    <div>
      Find countries: 
      <input 
      value={newFilter}
      onChange={handleFilter}
      />

      <Countries countries={countries}/>

    </div>
  )
}

export default App
