import React from 'react'

const Person = ({ name, number, removePerson, persons }) => {
  return (
    <div>
      {name}: {number} 
      <button onClick={() => removePerson(persons)}>delete</button>
    </div>
  )
}

const Persons = ({ persons, removePerson }) => {

  return (
    <>
      {persons.map(person => 
        <Person 
        name={person.name} 
        key={person.name} 
        number={person.number}
        removePerson={removePerson}
        persons={persons} />
      )}
    </>
  )
}

export default Persons
