const Countries = ({ countries, setNewFilter }) => {

  const Country = ({ country, singleCountry, setNewFilter }) => {

    useEffect(() => {
      console.log('Weather pending...')

      axios
        .get(`http://api.weatherapi.com/v1/forecast.json?key=${api_key}&q=${country}&days=1&aqi=no&alerts=no`)
        .then(response => {
          console.log(response.data)
          setWeather(response.data)
        })
      }, [])


    if (singleCountry) {
      return (
        <div>
          <h1>{country.name}</h1>

          <p>
            Capital: {country.capital}
            <br></br>
            Population: {country.population}
          </p>

          <h2>Languages</h2>
          <ul>
            {country.languages.map(language => 
              <li key={language.name}>{language.name}</li>)}
          </ul>
          <img 
          src={country.flag} 
          alt="Country's flag"
          width='170'
          ></img>
        </div>
      )
    } else {
      return (
    <div>
      {country.name}

      <input
      type='button'
      name={country.name}
      value='Show'
      onClick={() => 
        console.log('Button pressed.', country.name) 
        || setNewFilter(country.name)}/>
    </div>
      )
    }

  }


  if (countries.length > 10) {
    return (
      <div>
        Too many matches, specify another filter.
      </div>
    )
  } else if (countries.length === 1) {
    return (
      <>
        {countries.map(country => 
          <Country 
          country={country} 
          key={country.name} 
          singleCountry={true}
          setNewFilter={setNewFilter}
          />)
        }
      </>
    )
  } else {
    return (
      <div>
        {countries.map(country => 
          <Country 
          country={country} 
          key={country.name}
          singeCountry={false} 
          setNewFilter={setNewFilter}/>)
        }
      </div>
    )
  }
} 

export default Countries
